import { Component, Input, OnInit} from '@angular/core';
import {Product, products} from '../products';
import {Category, categories} from "../categories";
import {ActivatedRoute} from "@angular/router";

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit{
  products = [...products];
  category : Category;
  constructor(private route: ActivatedRoute) {
  }
  ngOnInit(): void {
    const thisCategory = String(this.route.snapshot.paramMap.get('name'));

    this.category = categories.find((x) => x.name === thisCategory)

  }
  share() {
    window.open("https://wa.me/?text=");
  }
  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }

  onRemove(currentName: string) {
    this.products = this.products.filter((x)=> x.name !== currentName)
  }

  onLiked(currentId: Product) {
      currentId.likes += 1;
    }
  }


